mapper.update:
UPDATE QNA_INQUIRE
   SET WRITER = #{writer}
     , ANSWER = #{answer}
     , ANSWER_TIME = #{answer_time}
     , REPLY = #{reply}
 WHERE COUNT =  #{count}

mapper.select:
SELECT USER_ID, USER_TYPE 
  FROM RESERVATION 
 WHERE USER_ID = #{user_id}
   AND USER_TYPE = #{user_type}
   AND PEN_ID = #{pen_id}
 GROUP BY USER_ID;
