mapper.update:
	UPDATE PENSION_ROOM SET 
	       RM_NAME = #{RM_NAME}
		  ,RM_CHECKOUT = #{RM_CHECKOUT}
		  ,RM_PRICE = #{RM_PRICE}
  	 WHERE ROOM_ID = #{RM_NUM}

mapper.select:
SELECT USER_NAME
     , USER_PHONENUMBER
  FROM MEMBER
 WHERE USER_ID = #{user_id}
   AND USER_TYPE = #{user_type}

mapper.update:
		 UPDATE REVIEW
		    SET REPLY = #{reply}
		      , ANS_CONTENT= #{ans_content}
		      , ANS_DATE = #{ans_date}
         WHERE REV_NUM = #{rev_num};
