mapper.insert:
INSERT INTO MEMBER
	 ( USER_ID
	 , USER_TYPE
	 , PASSWORD
	 , USER_NAME
     , JUMIN
     , USER_PHONENUMBER
     , USER_ADDRESS
	 , USER_ZIPCODE) 
VALUES
	 ( #{user_id} 
	 , #{user_type}
	 , #{password}
	 , #{user_name}
     , HEX(AES_ENCRYPT(#{jumin},'itwill'))
     , #{user_phonenumber}
     , #{user_address}
     , #{user_zipcode})

mapper.select:
SELECT USER_ID
     , USER_TYPE
     , USER_NAME
  FROM MEMBER
 WHERE USER_ID = #{user_id}
   AND USER_TYPE = #{user_type}

mapper.update:
UPDATE MEMBER 
   SET PASSWORD = #{password}
 WHERE USER_ID = #{user_id}
   AND USER_TYPE = #{user_type}

UPDATE MEMBER 
   SET USER_NAME = #{user_name}
     , PASSWORD = #{password}
     , USER_PHONENUMBER = #{user_phonenumber}
     , USER_ADDRESS = #{user_address}
 WHERE USER_ID = #{user_id}
   AND USER_TYPE = #{user_type}

mapper.delete:
DELETE
  FROM MEMBER
 WHERE USER_ID = #{user_id}
   AND USER_TYPE = #{user_type}
